import React from 'react'
import PropTypes from 'prop-types'
import '../task2.css'
import { MdClose } from 'react-icons/md'
import axios from 'axios'
import config from '../config/config'
import { Link } from 'react-router-dom'

function Card({ toLoad, value, boardState, setBoardState }) {
  const deleteCard = async () => {
    await axios.delete(
      `https://api.trello.com/1/boards/${value.id}?key=${config.key}&token=${config.token}`
    )
    const getBoards = axios.get(
      `https://api.trello.com/1/members/me/boards?key=${config.key}&token=${config.token}`
    )
    setBoardState(getBoards.data)
  }
  console.log(boardState)
  if (toLoad === false) {
    ;<div>
      loading ...<h1></h1>
    </div>
  }

  return (
    <div>
      <div
        className="homepage-card"
        style={{
          backgroundImage: `url(${value.prefs.backgroundImage})`,
          backgroundPosition: 'center',
          backgroundSize: 'cover',
          backgroundRepeat: 'no-repeat'
        }}
      >
        <Link
          key={value.id}
          to={`/dashBoard:${value.id}`}
          style={{ textDecoration: 'none' }}
        >
          <div className="homepage-card-data">
            <h1>{value.name}</h1>
          </div>
        </Link>
        <MdClose onClick={deleteCard} />
      </div>
    </div>
  )
}

Card.propTypes = {
  value: PropTypes.object.isRequired,
  toLoad: PropTypes.bool,
  boardState: PropTypes.array,
  setBoardState: PropTypes.func
}
export default Card
