import React, { useEffect, useState, useRef } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'
import Card from '../components/Card'
import NavBar from '../components/NavBar'

function HomePage() {
  const [homePageData, setHomePageData] = useState([])
  const [newBoardName, setBoardName] = useState('')
  const [isLoading, setLoading] = useState(true)
  const formStyling = useRef(null)
  useEffect(async () => {
    const getHomePageData = await axios.get(
      'https://api.trello.com/1/members/me/boards?key=4771cda06157329944b577f382bc2cf3&token=7a1c5dce94fc3f0026f3df596e11727bbb51730447ebc0e0f3732ccca73c5e86'
    )
    setHomePageData(getHomePageData.data)
    setLoading(false)
  }, [])
  const openForm = () => {
    formStyling.current.style.display = 'block'
  }
  const createNewBoard = async (e) => {
    e.preventDefault()
    console.log(newBoardName)
    await axios.post(
      `https://api.trello.com/1/boards/?key=4771cda06157329944b577f382bc2cf3&token=7a1c5dce94fc3f0026f3df596e11727bbb51730447ebc0e0f3732ccca73c5e86&name=${newBoardName}`
    )
    const updatedBoards = await axios.get(
      'https://api.trello.com/1/members/me/boards?key=4771cda06157329944b577f382bc2cf3&token=7a1c5dce94fc3f0026f3df596e11727bbb51730447ebc0e0f3732ccca73c5e86'
    )
    setHomePageData(updatedBoards.data)
  }
  const handleBoardName = (e) => {
    setBoardName(e.target.value)
  }

  if (isLoading) {
    return <div>App is Loading</div>
  }
  return (
    <div>
      <div className="home-page-container">
        <NavBar />
        {homePageData.map((ele) => {
          return (
            <Link key={ele.id} to={`/dashBoard:${ele.id}`}>
              <Card toLoad={isLoading} values={homePageData}></Card>
            </Link>
          )
        })}
      </div>
      <div className="homepage-card" onClick={openForm}>
        <h1>Create New Board</h1>
        <form
          className="add-board-form"
          ref={formStyling}
          onSubmit={createNewBoard}
        >
          <input
            type="text"
            placeholder="BoardName"
            onChange={handleBoardName}
          ></input>
          <input type="submit" value="Submit" />
        </form>
      </div>
    </div>
  )
}

export default HomePage
