import React, { useState, useEffect, useRef } from 'react'
import '../task2.css'
import { useParams } from 'react-router'
import axios from 'axios'
import NavBar from '../components/NavBar'

function Dashboard() {
  const { id } = useParams()
  const onlyId = id.split('').filter((ele) => ele !== ':')
  let finalId = ''
  onlyId.map((ele) => (finalId += ele))
  const formStyling = useRef(null)
  const [newBoardName, setBoardName] = useState('')
  const [currentState, changeCurrentState] = useState('')
  const [boardLists, getBoards] = useState([])
  const [boardListCards, getCardsForList] = useState([])
  const [load, checkLoad] = useState(true)
  useEffect(async () => {
    const listLink = `https://api.trello.com/1/boards/${finalId}/lists?key=4771cda06157329944b577f382bc2cf3&token=7a1c5dce94fc3f0026f3df596e11727bbb51730447ebc0e0f3732ccca73c5e86`
    console.log(listLink)
    const getLists = await axios.get(listLink)
    getBoards(getLists.data)
    const cardLink = `https://api.trello.com/1/boards/${finalId}/cards?key=4771cda06157329944b577f382bc2cf3&token=7a1c5dce94fc3f0026f3df596e11727bbb51730447ebc0e0f3732ccca73c5e86`
    const getCards = await axios.get(cardLink)
    getCardsForList(getCards.data)
    checkLoad(false)
  }, [])
  const openForm = () => {
    if (formStyling.current.style.display === 'block') {
      formStyling.current.style.display = 'none'
    } else {
      formStyling.current.style.display = 'block'
    }
  }
  const handleBoardName = (e) => {
    setBoardName(e.target.value)
  }
  const createNewBoard = async (e) => {
    e.preventDefault()
    console.log(newBoardName)
    await axios.post(
      `https://api.trello.com/1/boards/?key=4771cda06157329944b577f382bc2cf3&token=7a1c5dce94fc3f0026f3df596e11727bbb51730447ebc0e0f3732ccca73c5e86&name=${newBoardName}`
    )
    setBoardName('')
  }
  console.log(boardListCards)
  const handleChange = async (e, id) => {
    e.preventDefault()
    changeCurrentState(e.target.value)
    boardListCards.map((ele) => {
      if (ele.id === id) {
        ele.name = currentState
      }
      return ele
    })
  }
  if (load) {
    return <div>Wait Boards are loading</div>
  }
  return (
    <div className="dash-board-page">
      <NavBar />
      <div className="dash-board">
        {boardLists.map((ele) => {
          return (
            <div key={ele.id} className="board-list-container">
              <div key={ele.id} className="board-list-name">
                <h1>{ele.name}</h1>
              </div>
              <div className="list-cards">
                {boardListCards.map((element) => {
                  if (element.idList === ele.id) {
                    return (
                      <div className="cards" key={element.id}>
                        <input
                          type="text"
                          value={element.name}
                          onChange={(e) => handleChange(e, element.id)}
                        />
                      </div>
                    )
                  }
                })}
                <div>
                  <div className="add-button-wrapper">
                    <div className="add-new-board">
                      <div className="add-card" onClick={openForm}>
                        <h1>Create New Board</h1>
                      </div>
                      <form
                        className="add-board-form"
                        ref={formStyling}
                        onSubmit={createNewBoard}
                      >
                        <input
                          type="text"
                          placeholder="BoardName"
                          onChange={handleBoardName}
                        ></input>
                        <input type="submit" value="Submit" />
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )
        })}
      </div>
    </div>
  )
}

export default Dashboard
