import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'
import Card from '../components/Card'
import NavBar from '../components/NavBar'

function HomePage() {
  const [homePageData, setHomePageData] = useState([])
  const [isLoading, setLoading] = useState(true)
  useEffect(async () => {
    const getHomePageData = await axios.get(
      'https://api.trello.com/1/members/me/boards?key=4771cda06157329944b577f382bc2cf3&token=7a1c5dce94fc3f0026f3df596e11727bbb51730447ebc0e0f3732ccca73c5e86'
    )
    setHomePageData(getHomePageData.data)
    setLoading(false)
  }, [])
  if (isLoading) {
    return <div>App is Loading</div>
  }
  return (
    <div>
      <div className="home-page-container">
        <NavBar />
        {homePageData.map((ele) => {
          return (
            <Link key={ele.id} to={`/dashBoard:${ele.id}`}>
              <Card toLoad={isLoading} values={homePageData}></Card>
            </Link>
          )
        })}
      </div>
      <div className="homepage-card">
        <h1>Create New Board</h1>
      </div>
    </div>
  )
}

export default HomePage
