import React, { useState, useEffect } from 'react'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom'

// import NavBar from './components/NavBar'
import axios from 'axios'
import Dashboard from './pages/Dashboard'
import HomePage from './pages/HomePage'
import Card from './components/Card'

function Main() {
  const [homePageData, setHomePageData] = useState([])
  const [disp, setDisp] = useState(true)
  const [isLoading, setLoading] = useState(true)
  useEffect(async () => {
    const getHomePageData = await axios.get(
      'https://api.trello.com/1/members/me/boards?key=4771cda06157329944b577f382bc2cf3&token=7a1c5dce94fc3f0026f3df596e11727bbb51730447ebc0e0f3732ccca73c5e86'
    )
    setHomePageData(getHomePageData.data)
    setLoading(false)
  }, [])
  const handleDisp = () => {
    if (disp) {
      setDisp(false)
    } else {
      setDisp(true)
    }
  }

  if (isLoading) {
    return <div>App is Loading</div>
  }
  return (
    <Router>
      <div>
        <div className="navbar">
          <nav className="navbar">
            <Link to="/">
              <img
                src="/images/trello2.png"
                onClick={() => {
                  handleDisp()
                }}
              ></img>
            </Link>
          </nav>
        </div>
        {homePageData.map((ele) => {
          if (disp) {
            return (
              <Link
                key={ele.id}
                to={`/dashBoard:${ele.id}`}
                onClick={() => {
                  handleDisp()
                }}
              >
                <Card toLoad={isLoading} values={homePageData}></Card>
              </Link>
            )
          }
          return <div key={ele.id}></div>
        })}
      </div>
      <div>
        <Switch>
          <Route exact path="/" component={HomePage}>
            {setDisp}
          </Route>
          <Route path="/dashBoard:id" component={Dashboard}></Route>
        </Switch>
      </div>
    </Router>
  )
}
export default Main
