import React, { useState, useEffect } from 'react'
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'

// import NavBar from './components/NavBar'

import Dashboard from './pages/Dashboard'
import HomePage from './pages/HomePage'
import NavBar from './components/NavBar'
import axios from 'axios'

function Main() {
  const [currentSearch, setCurrentSearch] = useState('')
  const [homePageData, setHomePageData] = useState([])
  useEffect(() => {
    let mounted = true
    const fetch = async () => {
      const getHomePageData = await axios.get(
        `https://api.trello.com/1/members/me/boards?key=${config.key}&token=${config.token}`
      )
      if (mounted) {
        setLoadingState(false)
        setHomePageData(getHomePageData.data)
      }
    }

    fetch()

    return () => (mounted = false)
  }, [])
  return (
    <Router>
      <NavBar
        currentSearch={currentSearch}
        setCurrentSearch={setCurrentSearch}
      />
      <Switch>
        <Route exact path="/">
          <HomePage
            search={currentSearch}
            homePageData={homePageData}
            setHomePageData={setHomePageData}
          />
        </Route>
        <Route path="/dashBoard/:finalId" component={Dashboard}></Route>
      </Switch>
    </Router>
  )
}
export default Main
