import React, { useState, useEffect } from 'react'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom'

// import NavBar from './components/NavBar'
import axios from 'axios'
import Dashboard from './pages/Dashboard'
import HomePage from './pages/HomePage'
import Card from './components/Card'

function Main() {
  const [homePageData, setHomePageData] = useState([])
  const [isLoading, setLoading] = useState(true)
  useEffect(async () => {
    const getHomePageData = await axios.get(
      'https://api.trello.com/1/members/me/boards?key=4771cda06157329944b577f382bc2cf3&token=7a1c5dce94fc3f0026f3df596e11727bbb51730447ebc0e0f3732ccca73c5e86'
    )
    setHomePageData(getHomePageData.data)
    setLoading(false)
  }, [])

  if (isLoading) {
    return <div>App is Loading</div>
  }
  return (
    <Router>
      <div>
        <Link to="/"></Link>
        {homePageData.map((ele) => {
          return (
            <Link key={ele.id} to={`/dashBoard/${ele.id}`}>
              <Card toLoad={isLoading} values={homePageData}></Card>
            </Link>
          )
        })}
      </div>
      <div>
        <Switch>
          <Route exact path="/">
            <HomePage homeData={homePageData} isLoad={isLoading} />
          </Route>
          <Route exact path="/dashBoard:id" component={Dashboard}></Route>
        </Switch>
      </div>
    </Router>
  )
}
export default Main
