import React from 'react'
import PropTypes from 'prop-types'
import '../task2.css'
import { MdClose } from 'react-icons/md'
import axios from 'axios'
import config from '../config/config'
import { Link } from 'react-router-dom'
function Card({ toLoad, value }) {
  const deleteCard = async () => {
    await axios.delete(
      `https://api.trello.com/1/boards/${value.id}?key=${config.key}&token=${config.token}`
    )
  }
  if (toLoad === false) {
    ;<div>
      loading ...<h1></h1>
    </div>
  }

  return (
    <div>
      <div
        className="homepage-card"
        style={{
          backgroundImage: `url(${value.prefs.backgroundImage})`,
          backgroundPosition: 'center',
          backgroundSize: 'cover',
          backgroundRepeat: 'no-repeat'
        }}
      >
        <div className="homepage-card-data">
          <Link
            key={value.id}
            to={`/dashBoard:${value.id}`}
            style={{ textDecoration: 'none' }}
          >
            <h1>{value.name}</h1>
          </Link>
          <MdClose onClick={deleteCard} />
        </div>
      </div>
    </div>
  )
}

Card.propTypes = {
  value: PropTypes.object.isRequired,
  toLoad: PropTypes.bool
}
export default Card
