import React, { useState, useRef } from 'react'
import axios from 'axios'
import config from '../config/config'
import PropTypes from 'prop-types'

function AddCard({ listId }) {
  const formStyling = useRef(null)
  const [newListName, setListName] = useState('')
  const handleNewList = (e) => {
    setListName(e.target.value)
  }
  const addNewList = async (e) => {
    e.preventDefault()
    await axios.post(
      `https://api.trello.com/1/cards?key=${config.key}&token=${config.token}&idList=${listId}&name=${newCardName}`
    )
    setListName('')
    window.location.reload(false)
  }
  const openForm = () => {
    if (formStyling.current.style.display === 'block') {
      formStyling.current.style.display = 'none'
    } else {
      formStyling.current.style.display = 'block'
    }
  }
  return (
    <div>
      <div>
        <div className="add-button-wrapper">
          <div className="add-new-board">
            <div className="add-card" onClick={openForm}>
              <h1>Create New Card</h1>
            </div>
            <form
              className="add-board-form"
              ref={formStyling}
              onSubmit={addNewList}
            >
              <input
                type="text"
                placeholder="BoardName"
                onChange={handleNewList}
              ></input>
              <input type="submit" value="Submit" />
            </form>
          </div>
        </div>
      </div>
    </div>
  )
}
AddCard.propTypes = {
  listId: PropTypes.string
}

export default AddCard
