import React, { useState, useEffect } from 'react'
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'
// import HomePage from './pages/HomePage'
// import NavBar from './components/NavBar'
import axios from 'axios'
import Dashboard from './pages/Dashboard'
import HomePage from './pages/HomePage'

function Main() {
  const [homePageData, setHomePageData] = useState([])
  const [isLoading, setLoading] = useState(true)
  useEffect(async () => {
    const getHomePageData = await axios.get(
      'https://api.trello.com/1/members/me/boards?key=4771cda06157329944b577f382bc2cf3&token=7a1c5dce94fc3f0026f3df596e11727bbb51730447ebc0e0f3732ccca73c5e86'
    )
    setHomePageData(getHomePageData.data)
    setLoading(false)
  }, [])

  if (isLoading) {
    return <div>App is Loading</div>
  }
  return (
    <Router>
      <Switch>
        <Route exact path="/">
          <HomePage homeData={homePageData} isLoad={isLoading} />
        </Route>
        {homePageData.map((ele) => {
          return (
            <Route exact path={`/${ele.id}`} key={ele.id}>
              <Dashboard link={ele.id} />
            </Route>
          )
        })}
      </Switch>
    </Router>
  )
}
export default Main
