import React, { useState, useEffect } from 'react'
import '../task2.css'
import { useParams } from 'react-router'
import axios from 'axios'
import NavBar from '../components/NavBar'
import AddCard from '../components/AddCard'
import AddList from '../components/AddList'
import config from '../config/config'
import { MdModeEdit, MdDelete } from 'react-icons/md'

function Dashboard() {
  const { id } = useParams()
  const onlyId = id.split('').filter((ele) => ele !== ':')
  let finalId = ''
  onlyId.map((ele) => (finalId += ele))
  // const [currentState, changeCurrentState] = useState('')
  const [boardLists, getBoardsLists] = useState([])
  const [boardListCards, getCardsForList] = useState([])
  const [load, checkLoad] = useState(true)
  const [currentBoardName, setCurrentBoardName] = useState('')
  const [currentListName, setCurrentListName] = useState('')
  const [checkBoardName, setBoardName] = useState(false)
  const [checkListName, setListName] = useState(false)
  const [cardState, setCardState] = useState(false)
  useEffect(async () => {
    const getLists = await axios.get(
      `https://api.trello.com/1/boards/${finalId}/lists?key=${config.key}&token=${config.token}`
    )
    getBoardsLists(getLists.data)

    const getCards = await axios.get(
      `https://api.trello.com/1/boards/${finalId}/cards?key=${config.key}&token=${config.token}`
    )
    getCardsForList(getCards.data)

    const getCurrentBoardName = await axios.get(
      `https://api.trello.com/1/boards/${finalId}?key=${config.key}&token=${config.token}`
    )
    setCurrentBoardName(getCurrentBoardName.data.name)

    checkLoad(false)
  }, [])
  const handleBoardName = (e) => {
    setCurrentBoardName(e.target.value)
  }
  const handleListName = (e) => {
    setCurrentListName(e.target.value)
  }
  console.log(currentListName)
  const updateBoardName = async (e) => {
    e.preventDefault()
    try {
      await axios.put(
        `https://api.trello.com/1/boards/${finalId}?key=${config.key}&token=${config.token}&name=${currentBoardName}`
      )
      const getCurrentBoardName = await axios.get(
        `https://api.trello.com/1/boards/${finalId}?key=${config.key}&token=${config.token}`
      )
      setCurrentBoardName(getCurrentBoardName.data.name)
      setBoardName(false)
    } catch (e) {
      console.error(e)
    }
  }
  const updateListName = async (e, listId) => {
    e.preventDefault()
    try {
      await axios.put(
        `'https://api.trello.com/1/lists/${listId}?key=${config.key}&token=${config.token}&name=${currentListName}`
      )
      const getLists = await axios.get(
        `https://api.trello.com/1/boards/${finalId}/lists?key=${config.key}&token=${config.token}`
      )
      getBoardsLists(getLists.data)
    } catch (e) {
      console.error(e)
    }
  }

  const changeBoardState = () => {
    setBoardName(true)
  }
  const changeListState = (e, id) => {
    if (e.id === id) {
      setListName(true)
    }
  }
  const titleToList = (list) => {
    if (checkListName) {
      return (
        <div className="update-board-name" key={list.id}>
          <form
            onSubmit={(e, list) => {
              updateListName(e, list)
            }}
          >
            <input
              type="text"
              key={list.id}
              value={list.name}
              onChange={handleListName}
            ></input>
            <input type="submit" value="Change Name"></input>
          </form>
        </div>
      )
    } else {
      return (
        <div>
          <h1>{list.name}</h1>
        </div>
      )
    }
  }
  const boardName = checkBoardName ? (
    <div className="update-board-name">
      <form onSubmit={updateBoardName}>
        <input
          type="text"
          value={currentBoardName}
          onChange={handleBoardName}
        ></input>
        <input type="submit" value="Change Name"></input>
      </form>
    </div>
  ) : (
    <div>
      <h1>{currentBoardName}</h1>
    </div>
  )
  const deleteCard = async (cardId) => {
    try {
      await axios.delete(
        `https://api.trello.com/1/cards/${cardId}?key=${config.key}&token=${config.token}`
      )
      const getCards = await axios.get(
        `https://api.trello.com/1/boards/${finalId}/cards?key=${config.key}&token=${config.token}`
      )
      getCardsForList(getCards.data)
    } catch (e) {
      console.error(e)
    }
  }
  const upDateCard = (element) => {
    if (cardState) {
      return (
        <form>
          <input type="text" value={element.name} />
          <button type="submit" value={<MdModeEdit />}></button>
        </form>
      )
    } else {
      return <p>{element.name}</p>
    }
  }

  if (load) {
    return <div>Wait Boards are loading</div>
  }
  return (
    <div className="dash-board-page">
      <NavBar />
      <div onClick={changeBoardState} className="current-board">
        {boardName}
      </div>
      <div className="dash-board">
        {boardLists.map((ele) => {
          return (
            <div key={ele.id} className="board-list-container">
              <div
                className="board-list-name"
                onClick={(e) => changeListState(e, ele.id)}
              >
                {titleToList(ele)}
              </div>
              <div className="list-cards">
                {boardListCards.map((element) => {
                  if (element.idList === ele.id) {
                    return (
                      <div
                        className="cards"
                        key={element.id}
                        onClick={() => setCardState(true)}
                      >
                        {upDateCard(element)}
                        <div className="card-icons">
                          <MdModeEdit />
                          <MdDelete onClick={() => deleteCard(element.id)} />
                        </div>
                      </div>
                    )
                  } else {
                    return <div key={element.id}></div>
                  }
                })}
              </div>
              <AddCard
                key={ele.id}
                listId={ele.id}
                boardId={finalId}
                getCardsForList={getCardsForList}
              />
            </div>
          )
        })}
        <AddList boardId={finalId} getBoardsLists={getBoardsLists} />
      </div>
    </div>
  )
}

export default Dashboard
