import React, { useState, useEffect } from 'react'
import '../task2.css'
import { useParams } from 'react-router'
import axios from 'axios'

function Dashboard() {
  const { id } = useParams()
  const onlyId = id.split('').filter((ele) => ele !== ':')
  let finalId = ''
  onlyId.map((ele) => (finalId += ele))
  const [boardLists, getBoards] = useState([])
  const [boardListCards, getCardsForList] = useState([])
  const [load, checkLoad] = useState(true)
  useEffect(async () => {
    const listLink = `https://api.trello.com/1/boards/${finalId}/lists?key=4771cda06157329944b577f382bc2cf3&token=7a1c5dce94fc3f0026f3df596e11727bbb51730447ebc0e0f3732ccca73c5e86`
    console.log(listLink)
    const getLists = await axios.get(listLink)
    getBoards(getLists.data)
    const cardLink = `https://api.trello.com/1/boards/${finalId}/cards?key=4771cda06157329944b577f382bc2cf3&token=7a1c5dce94fc3f0026f3df596e11727bbb51730447ebc0e0f3732ccca73c5e86`
    const getCards = await axios.get(cardLink)
    getCardsForList(getCards.data)
    checkLoad(false)
  }, [])
  if (load) {
    return <div>Wait Boards are loading</div>
  }
  return (
    <div className="dash-board">
      {boardLists.map((ele) => {
        return (
          <div key={ele.id} className="board-list">
            <h1>{ele.name}</h1>
          </div>
        )
      })}
    </div>
  )
}

export default Dashboard
