import React, { useState } from 'react'
import PropTypes from 'prop-types'

function DashBoardLists(list) {
  const [currentListName, setCurrentListName] = useState(list.list.name)
  const listChangeHandler = (e) => {
    setCurrentListName(e.target.value)
  }

  console.log(list)
  return (
    <div>
      <div className="board-list-name">
        <form>
          <input
            type="text"
            value={currentListName}
            onChange={(e) => {
              listChangeHandler(e)
            }}
          />
        </form>
      </div>
    </div>
  )
}
DashBoardLists.propTypes = {
  list: PropTypes.object
}
export default DashBoardLists
